# function for game
def newGame():
    global word_withspace
    global noofguess
    noofguess=0
    imglabel.config(image=photos[0])
    imglabel.config(bg="white")
    the_word=random.choice(word_list)
    word_withspace=" ".join(the_word)
    lblword.set(" ".join("_"*len(the_word)))
    

def guess(letter):
    global noofguess
    if noofguess < 12:
        txt=list(word_withspace)
        guessed=list(lblword.get())
        if word_withspace.count(letter)>0:
            for c in range(len(txt)):
                if txt[c] == letter:
                    guessed[c]=letter
                lblword.set("".join(guessed))
                if lblword.get()==word_withspace:
                    messagebox.showinfo("Hangman","Congratulations, You Guess it")
                    back()
        else:
            noofguess+=1
            imglabel.config(image=photos[noofguess])
            if noofguess==12:
                    messagebox.showwarning("Hangman","Game Over")
                    back()


imglabel=Label(tk)
imglabel.grid(row=0,column=0,columnspan=3,padx=10,pady=40)
imglabel.config(image=photos[0])

lblword=StringVar()
Label(tk,textvariable=lblword,font=("Consolas 25 bold"),bg="white").grid(row=0,column=3,columnspan=6,padx=10)

n=0
for c in ascii_uppercase:
    Button(tk,text=c,command=lambda c=c:guess(c),font=("Helvetica",16,"bold"),width=7,bg='#4888aa',fg="white").grid(row=1+n//9,column=n%9)
    n+=1

